"use strict";

document.addEventListener('DOMContentLoaded', function () {
  // checker
  var useItemChecker = function useItemChecker(els, onClickOutside) {
    var checkBodyClick = function checkBodyClick(e) {
      var currentEl = e.target;
      while (currentEl) {
        if (els.includes(currentEl)) break;
        currentEl = currentEl.parentNode;
      }
      if (!currentEl) {
        onClickOutside();
        removeBodyChecker();
      }
    };
    function setBodyChecker() {
      document.documentElement.addEventListener('click', checkBodyClick);
    }
    function removeBodyChecker() {
      document.documentElement.removeEventListener('click', checkBodyClick);
    }
    return {
      setBodyChecker: setBodyChecker,
      removeBodyChecker: removeBodyChecker
    };
  };

  // inputmask
  Inputmask().mask(document.querySelectorAll('input'));

  // mobile menu
  var mobileMenu = document.getElementById('menu');
  var mobileMenuTrigger = document.getElementById('hamburger-toggle');
  var header = document.getElementById('header');
  if (header && mobileMenu && mobileMenuTrigger) {
    mobileMenuTrigger.addEventListener('click', function (event) {
      event.preventDefault();
      if (mobileMenu.classList.contains('is--active')) {
        header.classList.remove('is--menu');
        mobileMenuTrigger.classList.remove('is--active');
        mobileMenu.classList.remove('is--active');
        document.body.classList.remove('scroll-disabled');
      } else {
        header.classList.add('is--menu');
        mobileMenuTrigger.classList.add('is--active');
        mobileMenu.classList.add('is--active');
        document.body.classList.add('scroll-disabled');
      }
    });
  }

  // catalog filter
  var catalogFilter = document.querySelector('.catalog-filter');
  var catalogFilterTrigger = document.querySelector('.catalog-filter__trigger');
  if (catalogFilterTrigger) {
    var close = function close() {
      catalogFilter.classList.remove('is--active');
    };
    var itemChecker = useItemChecker([catalogFilter.parentNode], close);
    catalogFilterTrigger.addEventListener('click', function (event) {
      event.preventDefault();
      if (catalogFilter.classList.contains('is--active')) {
        close();
      } else {
        catalogFilter.classList.add('is--active');
        itemChecker.setBodyChecker();
      }
    });
  }

  // reviews slider
  var reviewsSlider = document.querySelector('.reviews__slider .swiper');
  if (reviewsSlider) {
    var myReviewsSlider = new Swiper(reviewsSlider, {
      slidesPerView: 2,
      spaceBetween: 20,
      loop: true,
      navigation: {
        prevEl: '.reviews__slider .swiper-button-prev',
        nextEl: '.reviews__slider .swiper-button-next'
      },
      breakpoints: {
        0: {
          slidesPerView: 'auto',
          spaceBetween: 16
        },
        768: {
          slidesPerView: 'auto',
          spaceBetween: 20
        },
        1024: {
          slidesPerView: 2,
          spaceBetween: 20
        }
      }
    });
  }

  // modals
  var modals = document.querySelectorAll('.modal');
  var modalsBtn = document.querySelectorAll('.modal-btn');
  var modalsClose = document.querySelectorAll('.modal__close');
  if (modals && modalsBtn && modalsClose) {
    // modals
    modals.forEach(function (item) {
      item.addEventListener('click', function (event) {
        if (event.target.classList.contains('modal') || event.target.classList.contains('modal__wrap')) {
          event.preventDefault();
          document.querySelectorAll('.modal.is--active').forEach(function (child) {
            return child.classList.remove('is--active');
          });
          document.body.classList.remove('scroll-disabled');
        }
      });
    });

    // modals btn
    modalsBtn.forEach(function (item, i) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        var modalID = item.dataset.id;
        document.querySelectorAll('.modal.is--active').forEach(function (child) {
          return child.classList.remove('is--active');
        });
        document.getElementById(modalID).classList.add('is--active');
        document.body.classList.add('scroll-disabled');
        if (mobileMenu.classList.contains('is--active')) {
          header.classList.remove('is--menu');
          mobileMenuTrigger.classList.remove('is--active');
          mobileMenu.classList.remove('is--active');
          document.body.classList.remove('scroll-disabled');
        }
      });
    });

    // modals close
    modalsClose.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        document.querySelectorAll('.modal.is--active').forEach(function (child) {
          return child.classList.remove('is--active');
        });
        document.body.classList.remove('scroll-disabled');
      });
    });
  }
});